import React, { Component } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, ActivityIndicator, Image, SafeAreaView, ScrollView, NatvigationsEvents } from 'react-native';

import Icon from 'react-native-vector-icons/Feather';

export default class Feed extends React.Component {
    static navigationOptions = {
        title: 'Feed',
    }

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            post: [],
        }

        this.getItems();
    }


    async getItems() {
        fetch('http://localhost:3000/view/listar.php')
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    isLoading: false,
                    post: responseJson,
                });
            }).catch((error) => {
                alert(error);
            });
    }


    render() {

        if (this.state.isLoading) {
            return (
                <View style={{ flex: 1, padding: 20 }}>
                    <ActivityIndicator />
                </View>
            )
        }

        return (
            <View>
                <TouchableOpacity onPress={() => this.props.navigation.navigate('Livros', { id: null, titulo: '', autor: ''})}>
                    <Text style={styles.adicionar_livro}>Cadastrar Livro</Text>
                </TouchableOpacity>
                {
                    this.state.post.map((item) => {
                        return (
                            <TouchableOpacity key={item.id} onPress={() => this.props.navigation.navigate('Livros', { id: item.id, titulo: item.titulo, autor: item.autor })}>
                                <View style={styles.header_post}>
                                    <Image source={{ uri: item.fotoperfil }} style={styles.tamanho_foto_perfil} />

                                    <View style={{ flex: 1 }}>
                                        <View style={{ paddingLeft: 8 }}>
                                            <Text style={styles.titulo_livro}>{item.titulo}</Text>
                                        </View>

                                        <View style={styles.desciption_position}>
                                            <Text style={styles.autor_livro}>{item.autor}</Text>
                                        </View>
                                    </View>

                                    <TouchableOpacity style={styles.more_post_option_position}>
                                        <Icon name="more-horizontal" color="black" size={20} />
                                    </TouchableOpacity>
                                </View>

                                <TouchableOpacity>
                                    <Image source={{ uri: item.imagemdopost }} style={styles.image_post_size} />
                                </TouchableOpacity>

                                <View style={{ padding: 20 }} />
                                <View style={styles.line} />
                            </TouchableOpacity>
                        )
                    }, { props: this.props })
                }
                <View style={{ padding: 40 }} />
            </View>
        );

    }

}

const styles = StyleSheet.create({
    tamanho_foto_perfil: {
        height: 50,
        width: 50,
        borderRadius: 100
    },

    titulo_livro: {
        fontSize: 16,
        fontWeight: 'bold',
    },

    autor_livro: {
        fontSize: 12,
        paddingLeft: 6
    },
    image_post_size: {
        height: 310,
        width: '100%',

    },

    header_post: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingTop: 16,
        paddingBottom: 10,
        marginHorizontal: 8,

    },

    line: {
        padding: 0.4,
        width: '100%',
        backgroundColor: '#F1F2F2',
    },

    adicionar_livro: {
        marginTop: 12,
        borderRadius: 100,
        backgroundColor: '#2398AB',
        padding: 20,
        fontSize: 20,
        alignSelf: 'center',
        color: 'white'
    },

})
