import React, { Component } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, ActivityIndicator, Image, SafeAreaView, ScrollView, NatvigationsEvents } from 'react-native';

import Icon from 'react-native-vector-icons/Feather';

export default class Feed extends React.Component {
    static navigationOptions = {
        title: 'Feed',
    }

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            post: [],
        }
        this.getItems();
    }


    getItems() { 
        fetch('http://localhost:3000/view/listar.php')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    linguagens: res
                });
            });
    }

    render() {
        return (
            <div>
                <h1>Lista de Livros</h1>

                <ul>
                    {this.state.linguagens.map(item => (
                        <li key={item.id}>
                            <p><b>TÃ­tulo:</b> {item.titulo}</p>
                            <p><b>Autor:</b> {item.autor}</p>
                        </li>
                    ))}
                </ul>
            </div>
        );
    }
}

