import React, { Component } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, ActivityIndicator, Image, SafeAreaView, ScrollView } from 'react-native';
import { NavigationEvents } from 'react-navigation';

import Icon from 'react-native-vector-icons/Feather';

export default class Feed extends React.Component {
    static navigationOptions = {
        title: 'Feed',
    }

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            post: [],
        }

        this.getItems();
    }


    async getItems() {
        fetch('http://192.168.0.10:3000/view/listar.php')
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    isLoading: false,
                    post: responseJson,
                });
            }).catch((error) => {
                alert(error);
            });
    }

    render() {

        if (this.state.isLoading) {
            return (
                <View style={{ flex: 1, padding: 20 }}>
                    <ActivityIndicator />
                </View>
            )
        }

        return (
            <View>
                <NavigationEvents onDidFocus={() => this.getItems()} />
                {
                    this.state.post.map((item) => {
                        return (
                            <TouchableOpacity key={item.id} onPress={() => this.props.navigation.navigate('Livros', { id: item.id, titulo: item.titulo, autor: item.autor})}>
                                <View style={styles.header_post}>

                                    <TouchableOpacity style={{ flex: 1 }}>
                                        <View style={{ paddingLeft: 6 }}>
                                            <Text style={styles.title_post}>TÃ­tulo:{item.titulo}</Text>
                                        </View>

                                        <View style={{ paddingLeft: 6 }}>
                                            <Text style={styles.title_post}>Autor: {item.autor}</Text>
                                        </View>
                                    </TouchableOpacity>

                                </View>

                                <View style={{ padding: 20 }} />
                                <View style={styles.line} />
                            </TouchableOpacity>
                        )
                    }, { props: this.props })
                }
                <View style={{ padding: 30 }} />
            </View>
        );

    }

}

const styles = StyleSheet.create({
    titulo: {
        fontSize: 16,
        fontWeight: 'bold',
    },

    desciption_post: {
        fontSize: 11,
        paddingLeft: 5
    },

    more_post_option_position: {
        flexDirection: 'row-reverse',
        marginEnd: 12,
    },

    image_post_size: {
        height: 310,
        width: '100%',

    },

    header_post: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingTop: 15,
        paddingBottom: 10,
        marginHorizontal: 7,

    },

    line: {
        padding: 0.4,
        width: '100%',
        backgroundColor: '#F1F2F2',
    },

    adicionar_Postagem: {
        marginTop: 12,
        borderRadius: 100,
        backgroundColor: '#2398AB',
        padding: 20,
        fontSize: 20,
        alignSelf: 'center',
        color: 'white'
    },

})
