import React, { Component } from 'react';
import { NavigationEvents } from 'react-navigation';
import { StyleSheet, View, Text, TouchableOpacity, ActivityIndicator } from 'react-native';

export default class Feed extends React.Component {
    static navigationOptions = {
        title: 'Feed',
    }

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            post: [],
        }
        this.getAPI();
    }

    async getAPI() {
        fetch('http://192.168.0.1:8000')
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    isLoading: false,
                    post: responseJson,
                });
            }).catch((error) => {
                alert(error);
            });
    }

    render() {

        if (this.state.isLoading) {
            return (
                <View style={{ flex: 1, padding: 20 }}>
                    <ActivityIndicator />
                </View>
            )
        }

        return (
            <View>
                <View>
                    <Text> Livros</Text>
                </View>
                <NavigationEvents onDidFocus={() => this.getAPI()} />
                {
                    this.state.post.map((item) => {
                        return (
                            <TouchableOpacity key={item.id} onPress={() => this.props.navigation.navigate('Livros', {
                                id: item.id, titulo: item.titulo, autor: item.autor, preco: item.preco, quantidade: item.quantidade
                            })}>
                                <View>

                                    <TouchableOpacity style={{ flex: 1 }}>
                                        <View>
                                            <Text>Título:{item.titulo}</Text>
                                        </View>

                                        <View>
                                            <Text>Autor: {item.autor}</Text>
                                        </View>

                                        <View>
                                            <Text>Preço: R$ {item.preco}</Text>
                                        </View>

                                        <View>
                                            <Text>Quantidade: {item.quantidade}</Text>
                                        </View>
                                    </TouchableOpacity>
                                </View>
                            </TouchableOpacity>
                        )
                    }, { props: this.props })
                }
                <View/>
            </View>
        );
    }
}
